//") ||  filename.startsWith("https://"))            return false;                if (filename.startsWith("zip://") || filename.endsWith(".zip") || filename.endsWith(".jar")) {            return true;        }        return false;    }    public boolean isTarFile(String filename) {        if (filename.startsWith("http://") ||  filename.startsWith("https://"))            return false;                if (filename.startsWith("tar://") ||  filename.endsWith(".tar")             ||  filename.endsWith(".tar.gz")||  filename.endsWith(".tar.z")            ||  filename.endsWith(".tar.bz2")) {            return true;        }        return false;    }}